mapper.select:

select 

from tmhanyo
where bunrui_cod = #{bunruiCod,jdbcType=VARCHAR}
  and kbn1 = #{kbn1,jdbcType=CHAR}
  and kbn2 = #{kbn2,jdbcType=CHAR}
  and tekiyostr_ymd = #{tekiyostrYmd,jdbcType=DATE}

mapper.delete:

delete from tmhanyo
where bunrui_cod = #{bunruiCod,jdbcType=VARCHAR}
  and kbn1 = #{kbn1,jdbcType=CHAR}
  and kbn2 = #{kbn2,jdbcType=CHAR}
  and tekiyostr_ymd = #{tekiyostrYmd,jdbcType=DATE}
